var res = _base64ToArrayBuffer(data.data);
if(res.length<9)
    return null;
var existSerial=['66413314','65656691','66413313', '66413315'];
var serialNumber1=null;
var serialNumber2=null;
var serialNumber3=null;
var serialNumber4=null;
