var waterWMBusValue1 = null;
var waterWMBusValue2 = null;
var waterWMBusValue3 = null;
var waterWMBusValue4 = null;
GetWMBusMeterValue(res[7], res[8], res[9], res[10], serialNumber, existSerial);
