configuration.tbelDecoder:
var data = decodeToJson(payload);
var deviceName = data.end_device_ids.device_id;
var deviceType = data.end_device_ids.application_ids.application_id;

var result = {};

if (data.?uplink_message.?decoded_payload.?Temperature != null) {
    result = {
        deviceName: deviceName,
        deviceType: deviceType,
        telemetry: {
            temperature: data.uplink_message.decoded_payload.Temperature,
            battery_status: data.uplink_message.decoded_payload["Battery Status"],
            rssi: data.uplink_message.rx_metadata[0].rssi,
            snr: data.uplink_message.rx_metadata[0].snr,
            spreading_factor: data.uplink_message.settings.data_rate.lora.spreading_factor
        }
    };
} else {
    result = {
        deviceName: deviceName,
        deviceType: deviceType,
        telemetry: {
            rssi: data.uplink_message.rx_metadata[0].rssi,
            snr: data.uplink_message.rx_metadata[0].snr,
            spreading_factor: data.uplink_message.settings.data_rate.lora.spreading_factor
        }
    };
}

return result;
